mapper.select:
SELECT p.id, p.title, p.summary, p.create_time, p.update_time, prc.read_count, pl.like_count, case when temp.comment_count is null then 0 else temp.comment_count end as comment_count FROM paper p INNER JOIN paper_like pl on p.id = pl.paper_id INNER JOIN paper_read_count prc on p.id = prc.paper_id LEFT JOIN ( SELECT count(*) AS comment_count, pc.paper_id FROM paper_comment pc WHERE pc.active = 1 and pc.enable = 1 GROUP BY paper_id ) temp on temp.paper_id = p.id
p.enable = 1 and p.active = 1
p.title = #{title}

mapper.update:
UPDATE paper t
SET t.enable = 1 - t.enable
WHERE t.id = #{paper_id}

UPDATE paper t
SET t.active = 0
WHERE t.id = #{paper_id}
